#include <stdint.h>

#if !defined(__SOFT_FP__) && defined(__ARM_FP)
  #warning "FPU is not initialized, but the project is compiling for an FPU. Please initialize the FPU before use."
#endif

int main(void)
{
    /* Loop forever */
	//SFR pointers
	uint32_t *pClkRegister  = (uint32_t *)(0x40023800 + 0x30); //RCC_AHB1ENR
	uint32_t *pGPIOA_MODER  = (uint32_t *)(0x40020000 + 0x00); //GPIOA_MODER
	uint32_t *pGPIOD_MODER  = (uint32_t *)(0x40020C00 + 0x00); //GPIOD_MODER
	uint32_t *pGPIOA_IDR    = (uint32_t *)(0x40020000 + 0x10); //GPIOA_IDR
	uint32_t *pGPIOD_ODR    = (uint32_t *)(0x40020C00 + 0x14); //GPIOD_ODR
	uint8_t pinStatus;
	//Enable clock for GPIOA and GPIOD peripherals
	*pClkRegister |=  (0x09 << 0);
	//Set pin modes
	*pGPIOD_MODER &=  ~(0x55 << 24);
	*pGPIOD_MODER |=  (0x55 << 24);
	*pGPIOA_MODER &= ~(0x03 << 0);
//	*pGPIOA_MODER &= ~(0xFF << 24);
	while(1)
	{
		pinStatus = (uint8_t)(*pGPIOA_IDR & 0x01);
		if(pinStatus)
			*pGPIOD_ODR |= (0x0F << 12);
		else
			*pGPIOD_ODR &= ~(0x0F << 12);
	}
}
